						$dataFeedElement->url = $query['url'];
					}
					if ($mimetype == 'application/pdf')
					{
						$dataFeedElement->pdf = $query['url'];
					}					
					
					// Google Scholar cluster id
					$dataFeedElement->{'@id'}  = 'https://scholar.google.com/scholar?cluster=' . $query['d'];
				}
			}
			
