$obj = json_decode($json);

foreach ($obj->results as $result)
{
	$dataFeedElement = new stdclass;
	$dataFeedElement->id = $result->fqId;
	$dataFeedElement->url = 'https://www.ipni.org' . $result->url;
	
	$dataFeedElement->name = $result->name;
	
	
	$dataFeedElement->item = new stdclass;
	
	if (isset($result->publication))
	{
		add_to_item($dataFeedElement->item, 'journal', $result->publication);
	}
	
	if (isset($result->referenceCollation))
	{
		add_to_item($dataFeedElement->item, 'pages', $result->referenceCollation);
	}
	
	if (isset($result->publicationYearNote))
	{
		if (preg_match('/(\d+ [A-Z][a-z]+ [0-9]{4})/', $result->publicationYearNote, $m))
		{
			$dataFeedElement->item->datePublished = date_format( date_create_from_format('d M Y', $m[1]), 'Y-m-d');
			// echo $m[1] . '|' . $dataFeedElement->item->datePublished . "\n";
		}
		
	}
	else
	{
		if (isset($result->publicationYear))
		{
			$dataFeedElement->item->datePublished = $result->publicationYear . "-00-00";
		}		
	}
	
	if (isset($dataFeedElement->item->datePublished))
	{
		$dataFeedElement->datePublished = $dataFeedElement->item->datePublished;
	}
	
	if (isset($result->remarks))
	{
		if (preg_match('/doi:(10\.\d+\/[^\s]+)/', $result->remarks, $m))
		{
			$dataFeedElement->item->doi = strtolower($m[1]);
		}
	}
	
	
	$terms =array();
	
	if (isset($result->reference))
	{
		$terms[] = $result->reference;
	}
	if (isset($result->distribution))
	{
		$terms[] = $result->distribution;
	}
	if (isset($result->locality))
	{
		$terms[] = $result->locality;
	}	
	
	// create a description
	if (count($terms) > 0)
	{
		$dataFeedElement->description = join(' ', $terms);
	}
	
	
	$dataFeed->dataFeedElement[] = $dataFeedElement;

}

